    ]
}
}}

parameters.jsCode:
// The LLM returns a JSON string, we need to parse it into a real object
// that n8n can use, specifically for the Split in Batches node.
const aiResponseString = $json.choices[0].message.content;

try {
  const parsedJson = JSON.parse(aiResponseString);
  // The prompt asks for an object { "clips": [...] }
  // We return just the array for the Split in Batches node.
  if (!parsedJson.clips || !Array.isArray(parsedJson.clips)) {
    throw new Error('AI response does not contain a valid clips array');
  }
  return parsedJson.clips;
} catch (error) {
  throw new Error(`Failed to parse AI response. The LLM did not return valid JSON. Response was: ${aiResponseString}`);
}

const fs = require('fs');

// Get the full word-level transcript from the Whisper node
// We use .$node because this data is from before the Split in Batches loop started
const whisperNodeOutput = $items("6. Transcribe Audio (Whisper)")[0].json;

// Check if the expected 'words' property exists
if (!whisperNodeOutput || !whisperNodeOutput.words) {
  throw new Error("Could not find word-level transcript in the output from the Whisper node. Full output: " + JSON.stringify(whisperNodeOutput));
}

const fullTranscript = whisperNodeOutput.words;

// Get the current clip's info from the loop
const clip = $json;
const startTime = clip.start_time;
const endTime = clip.end_time;

// Filter the transcript to get only the words within this clip's timeframe
const clipWords = fullTranscript.filter(word => word.start >= startTime && word.end <= endTime);

// Helper function to format seconds into SRT timestamp format (HH:MM:SS,ms)
function formatSrtTime(seconds) {
  const date = new Date(0);
  date.setSeconds(seconds);
  const timeStr = date.toISOString().substr(11, 12);
  return timeStr.replace('.', ',');
}

let srtContent = '';
// Create SRT entries in batches of a few words to make captions more readable
const wordsPerCaption = 5;
for (let i = 0; i < clipWords.length; i += wordsPerCaption) {
    const chunk = clipWords.slice(i, i + wordsPerCaption);
    if (chunk.length === 0) continue;

    const srtIndex = (i / wordsPerCaption) + 1;
    const srtStart = formatSrtTime(chunk[0].start);
    const srtEnd = formatSrtTime(chunk[chunk.length - 1].end);
    const srtText = chunk.map(w => w.word).join(' ');

    srtContent += `${srtIndex}\n${srtStart} --> ${srtEnd}\n${srtText}\n\n`;
}

// Write the SRT content to a file
// The file path comes from the node that created the run folder
const runPath = $items("2. Set Paths & Run ID")[0].json.run_path;
const srtFilePath = `${runPath}/clip_${$loop.iterations}.srt`;

fs.writeFileSync(srtFilePath, srtContent);

// Return the path to the SRT file for the next node
return { srt_file_path: srtFilePath };

